    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed
        String cargo = null;
        int qtdNum = 0;
        
        if (jRadioButton1.isSelected()) {
            cargo = "Prefeito";
            qtdNum = 2;
        } else if (jRadioButton2.isSelected()) {
            cargo = "Vereador";
            qtdNum = 5;
        }
        String cp1 = campoNumero.getText();
        String cp2 = campoNome.getText();
        String cp3 = campoPartido.getText();

        if (cargo.isEmpty() || cp1.isEmpty() || cp2.isEmpty() || cp3.isEmpty() || cp1.length() != qtdNum) {
            JOptionPane.showMessageDialog(null, "Por favor preencha os campos.", "Erro", JOptionPane.ERROR_MESSAGE);
        } else {
            cadastroCandidatos(cp1, cp2, cp3, cargo, c.dir);
            if (c.deuCerto) {
                JOptionPane.showMessageDialog(null, "Cadastro efetuado com sucesso.", "Sucesso", JOptionPane.DEFAULT_OPTION);
                campoNumero.setText("");
                campoNome.setText("");
                campoPartido.setText("");
                lblImage.setIcon(null);
                btnAdicionar.setEnabled(true);
                
            } else {
                JOptionPane.showMessageDialog(null, "Erro ao cadastrar o candidato.", "Erro", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_btnSalvarActionPerformed

